const initalColorState = 0;
const colorReducer = (state = initalColorState, action)=>{

    switch(action.type){
        case 'increaseShade' : 
         if(state < 10)
         {
            return state + 1;
         }
         case 'decreaseShade' :
            if(state > 0)
            {
               return state - 1;
            }
            default : return state;
    }


}
