const initalColorState = 2;
const colorReducer = (state = initalColorState, action)=>{

    switch(action.type){
        case 'increaseShade' : 
         if(state < 10)
         {
            return state + action.payload;
         }
         break;
         case 'decreaseShade' :
            if(state > 0)
            {
               return state - action.payload;
            }
            break;
            default : return state;
    }


}

export default colorReducer;
